  return [{
    json: {
      error: 'Failed to parse payload JSON: ' + error.message,
      user: {},
      profile: {},
      settings: {},
      weeklyData: weeklyData
    }
  }];
}

// –ò–∑–≤–ª–µ–∫–∞–µ–º –æ—Å–Ω–æ–≤–Ω—ã–µ —Å–µ–∫—Ü–∏–∏
const user = parsedData.user || {};
const profile = parsedData.profile || {};
