// –û–±—Ä–∞–±–æ—Ç–∫–∞ –¥–∞–Ω–Ω—ã—Ö –∏–∑ meals_added (–∫–∞–∫ –≤ –æ—Ä–∏–≥–∏–Ω–∞–ª–µ)
console.log('=== –£–ü–†–û–©–ï–ù–ù–ê–Ø –û–ë–†–ê–ë–û–¢–ö–ê MEALS ===');

// –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–π –Ω–æ–¥—ã Get meals_added
let rawData = $input.all();
console.log('–ü–æ–ª—É—á–µ–Ω–æ –∑–∞–ø–∏—Å–µ–π:', rawData.length);

// –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å–ª–∏ –¥–∞–Ω–Ω—ã–µ –ø—Ä–∏—Ö–æ–¥—è—Ç –≤ –æ–±–µ—Ä—Ç–∫–µ
let mealsData = rawData;
if (rawData.length === 1 && Array.isArray(rawData[0])) {
  mealsData = rawData[0];
} else if (rawData.length === 1 && rawData[0].json && Array.isArray(rawData[0].json)) {
  mealsData = rawData[0].json;
}

console.log('–§–∏–Ω–∞–ª—å–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –±–ª—é–¥ –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏:', mealsData.length);

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –±–µ–∑–æ–ø–∞—Å–Ω–æ–≥–æ –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏—è –≤ —á–∏—Å–ª–æ
const safeFloat = (value) => {
  if (value === null || value === undefined || value === '') {
    return 0;
  }
  const num = parseFloat(value);
  return isNaN(num) ? 0 : num;
};

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –∫–∞–∂–¥–æ–µ –±–ª—é–¥–æ —Å —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω—ã–º–∏ –Ω—É—Ç—Ä–∏–µ–Ω—Ç–∞–º–∏
const processedMeals = mealsData.map((item, index) => {
  let meal = item;
  if (item.json) {
    meal = item.json;
  }

  const dishName = meal.dish || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ–µ –±–ª—é–¥–æ';
  const calories = safeFloat(meal.kcal);
  const protein = safeFloat(meal.prot);
  const fat = safeFloat(meal.fat);
  const carbs = safeFloat(meal.carb);
  const sugar = safeFloat(meal.sugar);
  const fiber = safeFloat(meal.fiber);
  const sodium = safeFloat(meal.sodium);
  const healthScore = safeFloat(meal.overallhealthscore);

  return {
    dish: dishName,
    calories: calories,
    protein: protein,
    fat: fat,
    carbs: carbs,
    sugar: sugar,
    fiber: fiber,
    sodium: sodium,
    healthScore: healthScore
  };
});

// –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º –æ–±—â—É—é —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É
const totalCalories = processedMeals.reduce((sum, meal) => sum + meal.calories, 0);
const averageCalories = processedMeals.length > 0 ? Math.round(totalCalories / processedMeals.length) : 0;

// –§–æ—Ä–º–∏—Ä—É–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
const result = {
  meals: processedMeals,
  summary: {
