console.log('–û–±—â–∏–µ –∫–∞–ª–æ—Ä–∏–∏:', result.summary.totalCalories);

return [{ json: result }];

// –û–±—ä–µ–¥–∏–Ω–µ–Ω–∏–µ –≤—Å–µ—Ö –¥–∞–Ω–Ω—ã—Ö + –µ–∂–µ–Ω–µ–¥–µ–ª—å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞
console.log('=== –û–ë–™–ï–î–ò–ù–ï–ù–ò–ï –î–ê–ù–ù–´–• –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–Ø + WEEKLY CHECK ===');

// –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –≤—Å–µ—Ö –ø—Ä–µ–¥—ã–¥—É—â–∏—Ö –Ω–æ–¥
const webhookProcessedData = $('process_webhook_events').all();
const profilesData = $('Get profiles').all();
const mealsProcessedData = $('Code').all();
const originalRequest = $('Webhook - Weekly Check').all()[0] || {};

console.log('Webhook processed data:', webhookProcessedData.length, '–∑–∞–ø–∏—Å–µ–π');
console.log('Profiles data:', profilesData.length, '–∑–∞–ø–∏—Å–µ–π');
console.log('Meals processed data:', mealsProcessedData.length, '–∑–∞–ø–∏—Å–µ–π');

// –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –¥–∞–Ω–Ω—ã—Ö
if (webhookProcessedData.length === 0) {
  console.error('‚ùå –ù–µ—Ç –æ–±—Ä–∞–±–æ—Ç–∞–Ω–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö webhook');
  return [{ json: { error: 'No processed webhook data' } }];
}

// –ò–∑–≤–ª–µ–∫–∞–µ–º –æ–±—Ä–∞–±–æ—Ç–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
const webhookData = webhookProcessedData[0].json || webhookProcessedData[0];
const profileData = profilesData.length > 0 ? (profilesData[0].json || profilesData[0]) : {};
const mealsData = mealsProcessedData.length > 0 ? (mealsProcessedData[0].json || mealsProcessedData[0]) : { meals: [], summary: { totalCalories: 0 } };

// –ü–æ–ª—É—á–∞–µ–º userId
const userId = originalRequest.body?.userId || originalRequest.userId || webhookData.user?.emailId;
console.log('User ID:', userId);

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –¥–µ—Ç–∞–ª—å–Ω–æ–π —Å–≤–æ–¥–∫–∏ –ø–∏—Ç–∞–Ω–∏—è —Å —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω—ã–º–∏ –Ω—É—Ç—Ä–∏–µ–Ω—Ç–∞–º–∏
const createDetailedNutritionSummary = (simpleMealsData) => {
  if (!simpleMealsData || !simpleMealsData.meals) {
    return {
      totalCalories: 0,
      avgCaloriesPerDay: 0,
      mealCount: 0,
      allDishes: [],
      totalProtein: 0,
      totalFat: 0,
      totalCarbs: 0,
      totalSugar: 0,
      totalFiber: 0,
      avgHealthScore: 0
    };
  }

  const rawMeals = simpleMealsData.meals || [];
  const totalCalories = rawMeals.reduce((sum, meal) => sum + meal.calories, 0);
  const totalProtein = rawMeals.reduce((sum, meal) => sum + meal.protein, 0);
  const totalFat = rawMeals.reduce((sum, meal) => sum + meal.fat, 0);
  const totalCarbs = rawMeals.reduce((sum, meal) => sum + meal.carbs, 0);
  const totalSugar = rawMeals.reduce((sum, meal) => sum + meal.sugar, 0);
  const totalFiber = rawMeals.reduce((sum, meal) => sum + meal.fiber, 0);
  const avgHealthScore = rawMeals.length > 0 ? Math.round(rawMeals.reduce((sum, meal) => sum + meal.healthScore, 0) / rawMeals.length) : 0;

  const mealCount = rawMeals.length;
  const estimatedDays = Math.max(1, Math.min(7, Math.ceil(mealCount / 3)));

  return {
    totalCalories: totalCalories,
    avgCaloriesPerDay: Math.round(totalCalories / estimatedDays),
    mealCount: mealCount,
    totalProtein: Math.round(totalProtein),
    totalFat: Math.round(totalFat),
    totalCarbs: Math.round(totalCarbs),
    totalSugar: Math.round(totalSugar),
    totalFiber: Math.round(totalFiber),
    avgHealthScore: avgHealthScore,
    allDishes: rawMeals.map(meal => ({
      dish: meal.dish,
      calories: meal.calories,
      protein: meal.protein,
      healthScore: meal.healthScore
    }))
  };
};

// –§–æ—Ä–º–∏—Ä—É–µ–º —Ñ–∏–Ω–∞–ª—å–Ω—ã–µ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
const structuredData = {
  user: {
    emailId: userId,
    age: webhookData.user?.age || null,
    timezone: profileData.timezone || webhookData.user?.timezone || 'UTC',
    timezoneOffset: profileData.timezone_offset || webhookData.user?.timezoneOffset || 0,
    locale: profileData.locale || 'en',
    country: profileData.country || 'Unknown'
  },
  profile: {
    // –í—Å–µ –ø–æ–ª—è –ø—Ä–æ—Ñ–∏–ª—è –∫–∞–∫ –≤ –æ—Ä–∏–≥–∏–Ω–∞–ª–µ
    gender: webhookData.profile?.gender || null,
    height: webhookData.profile?.height || null,
    currentWeight: webhookData.weeklyData?.currentWeight || webhookData.profile?.currentWeight || null,
    goalWeight: webhookData.profile?.goalWeight || null,
    weightLossRate: webhookData.profile?.weightLossRate || null,
    primaryGoal: webhookData.profile?.primaryGoal || null,
    activityLevel: webhookData.profile?.activityLevel || null,
    dietPreference: webhookData.profile?.dietPreference || null,
    nutritionFocus: webhookData.profile?.nutritionFocus || null,
    mealFrequency: webhookData.profile?.mealFrequency || null,
    foodPreferences: webhookData.profile?.foodPreferences || null,
    foodVariety: webhookData.profile?.foodVariety || null,
    weightLossPlan: webhookData.profile?.weightLossPlan || null,
    exerciseIntent: webhookData.profile?.exerciseIntent || null,
    confidenceLevel: webhookData.profile?.confidenceLevel || null,
    challenges: webhookData.profile?.challenges || [],
    mainObstacle: webhookData.profile?.mainObstacle || null,
    stressResponse: webhookData.profile?.stressResponse || null,
    adaptability: webhookData.profile?.adaptability || null,
    decisionMaking: webhookData.profile?.decisionMaking || null,
    temptationResponse: webhookData.profile?.temptationResponse || null
  },
  settings: {
