    heightUnit: webhookData.settings?.heightUnit || 'cm',
    system: webhookData.settings?.system || 'metric'
  },
  weeklyNutrition: createDetailedNutritionSummary(mealsData),

  // –ò–°–ü–†–ê–í–õ–ï–ù–û: –î–∞–Ω–Ω—ã–µ —Ç–µ–∫—É—â–µ–π –µ–∂–µ–Ω–µ–¥–µ–ª—å–Ω–æ–π –ø—Ä–æ–≤–µ—Ä–∫–∏
  currentWeeklyCheck: {
    currentWeight: webhookData.weeklyData?.currentWeight || null,
    energyLevel: webhookData.weeklyData?.energyLevel || null,
    motivationLevel: webhookData.weeklyData?.motivationLevel || null,
    challenges: webhookData.weeklyData?.challenges || [],
    dietCompliance: webhookData.weeklyData?.dietCompliance || null,
    exerciseCompliance: webhookData.weeklyData?.exerciseCompliance || null,
    sleepQuality: webhookData.weeklyData?.sleepQuality || null,
    stressLevel: webhookData.weeklyData?.stressLevel || null,
    completedAt: webhookData.weeklyData?.completedAt || new Date().toISOString()
  }
};

console.log('=== –§–ò–ù–ê–õ–¨–ù–´–ï –û–ë–™–ï–î–ò–ù–ï–ù–ù–´–ï –î–ê–ù–ù–´–ï ===');
console.log('User:', structuredData.user);
console.log('Weekly Check –¥–∞–Ω–Ω—ã–µ:', structuredData.currentWeeklyCheck);
console.log('Nutrition summary:', {
  totalCalories: structuredData.weeklyNutrition.totalCalories,
